import React, { Component } from 'react';

class Todolist extends React.Component{
    render(){
        return(
            <div className='board'>
                <div className='todo'>
                    <h1>todo</h1>
                    <Checkarea value = {this.props.value} />
                </div>
                <div className='in-progress'>
                    <h1>in-progress</h1>
                </div>
                <div className='done'>
                    <h1>done</h1>
                </div>
            </div>

        ); 
    }
}

class Checkarea extends Component{
    constructor(props){
        super(props);
        const area=[];
        for(let i = 0; i < this.props.value.todos.length; i++){
            if (this.props.value.todos[i].area === 1){
                area.push(todos[i].name)
            }
        }
    }

    render(){
        return(
        <div className='task'>
            {this.area.name}
        </div>
        );

    }
}

export default Todolist;
