import React, { Component } from 'react';

class Todo extends Component{
  render(){ 
      return(
          <div className="todos">
              {this.props.value
.filter(i=>i.area===this.props.area)
.map(i=>{return <div className="todo" onClick={()=>{console.log(i)}}>{i.name}</div>})}
          </div>
      );
  }
}
export default Todo;

