import React from 'react';

class Todolist extends React.Component{
    render(){
        return(
            <div className='todo'>
                <h1>todo</h1>
            </div>
        );
        if (this.props.value.todos[0].area === 1){
            return(
                <div className='task'>
                    {this.props.value.todos[0].name}
                </div>
            );
        }


    }
}
export default Todolist;
