import React, { Component } from 'react';

class Todolist extends React.Component{
    render(){
        return(
            <div className='board'>
                <div className='todo'>
                    <h1>todo</h1>
                    <Checkarea 
                    value = {this.props.value}
                    max = {this.props.value.todos.length}
                    />
                </div>
                <div className='in-progress'>
                    <h1>in-progress</h1>
                </div>
                <div className='done'>
                    <h1>done</h1>
                </div>
            </div>

        ); 
    }
}

class Checkarea extends Component{
    render(){
        for(let i = 0; i < max; i++){
            if (this.props.value.todos[i].area === 1){
                return(
                    <div className='task'>
                        {this.props.value.todos[i].name}
                    </div>
                );
            }
        }
    }
}

export default Todolist;
